INSERT INTO Story (name, description)
VALUES
    ('Таємниця лісу', 'Ця історія розповідає про загадкові події, що трапилися в старому лісі. Читачі зануряться в атмосферу таємничості та розкриють непередбачувані сюжетні повороти. Головний персонаж, молода дослідниця Віолетта, відправляється у подорож у глибину лісу, де вона натраплює на давно забуті таємниці, які загрожують змінити її життя назавжди. Її шлях переплітається з втраченим світом феї та старовинними магічними ритуалами, відкриваючи нові реалії, про які вона і не мріяла.'),
    ('Підводний світ', 'Відкрийте для себе захоплюючі пригоди групи дослідників, які вирушають у відкриття підводного світу. Вас очікують захоплюючі зустрічі з морськими істотами та величні пейзажі океану. Головний герой, вчений океанограф Джейк, разом зі своєю командою вирушає у небезпечну експедицію на дно океану, де вони зіштовхуються з неймовірними випробуваннями та відкривають нові види морських істот, які до цього часу були невідомі науці. Але найбільша загадка чекає їх у найглибшій частині океану, де вони зіткнуться з легендарним містичним суперником, що прагне засвоїти секрети підводного світу.'),
    ('Загублене місто', 'Ця історія розповідає про експедицію археологів, які вирушають у пошуках загубленого міста, що зникло віками тому. Під час своєї подорожі вони стикаються з небезпеками джунглів, загадковими загубленими технологіями та давніми прокляттями. Але справжнє випробування чекає їх, коли вони відкривають ворота до загубленого міста, де вони розкривають таємниці, які не повинні бути відомі людям. Чи зможуть вони вижити в цьому вигнаному місці, де час виглядає зупинився?'),
    ('Вогняна пустеля', 'Приготуйтеся до пригод у найнебезпечнішій місцевості планети - вогняній пустелі, де кожен день - боротьба за виживання. Герої цієї історії шукають втрачений артефакт, який може змінити хід історії, але вони не одні, хто прагне здобути його. Пустеля приховує багато таємниць та небезпек, і лише найсміливіші зможуть дістатися до цілі. Супротивниками героїв стануть не лише природа та випробування, але й інші підступні суперники, які прагнуть увірватися першими.'),
    ('Міжзірковий колонізатор', 'У цьому науково-фантастичному епосі вируште на простори космосу разом із групою колонізаторів, які прагнуть заснувати нове поселення на віддаленій планеті. Але чи вдасться їм вижити у цьому безмежному й небезпечному космічному просторі? Під час своєї місії вони зіткнуться зі зловітими, хто прагне перешкодити їхнім планам, зі своїми власними амбіціями та мотивами. Вони зіткнуться із випробуваннями, що ставлять під сумнів не лише їх фізичні, але й моральні межі. Але серед безмежної темряви космосу вони також знайдуть справжніх союзників, здатних допомогти їм у цій небезпечній подорожі і забезпечити їхній майбутній успіх і виживання.'),
    ('Пірати космосу', 'У світі, де космічні кораблі - нові кораблі піратів, герої цієї історії вступають у боротьбу за свободу та владу над космічними просторами. Вони шукають скарби та пригоди, але кожна зустріч може бути останньою. Суперники готові за все, щоб перешкодити їхнім планам і викрасти все, що вони здобули. Але пірати не здаються легко, вони готові боротися до останнього подиху за свої права та свободу.'),
    ('Магія віків', 'У світі, де магія є повсякденністю, молодий чаклун вирушається у подорож у пошуках втраченого артефакту, який здатний змінити хід вікової війни між силами добра та зла. Але вороги не сплять, і кожен крок приносить нові випробування та небезпеки. У своїй місії він зіштовхнеться із загрозами, які ставлять під сумнів не лише його вміння, але й його віру у праведність.'),
    ('Герцогство тіней', 'У цьому темному фентезійному світі, де тіні мають життя власне, герой вирушає на відважний пошук своєї загубленої коханої, яка потрапила в обійми зловісного владаря Герцогства тіней. Чи зможе він перемогти темну магію та врятувати кохану? Його шлях переплітається з небезпеками, що чатують у кожному кутку, і з давніми загадками, які він повинен розгадати, щоб врятувати не лише своє щастя, але й життя своєї коханої.'),
    ('Шлях воїна', 'У цій історії молодий воїн вирушає на пошуки свого майстерства та справжнього значення своєї долі. Під час свого подорожування він зіштовхується зі злом та випробуваннями, які допомагають йому зрости як боєць та людина. Його шлях переплітається з історіями великих воїнів минулого та з іншими сміливими душами, які прагнуть змінити світ. Але чи зможе він знайти в собі силу, щоб пройти цей шлях до кінця і знайти своє місце у світі?'),
    ('Хроніки зоряного магістеріуму', 'У цьому світі магії та технології, де зірки - це ключ до найвеличніших таємниць, герої вирушають на пошуки втрачених знань та джерела всевладності. Але їхній шлях пролягає через небезпеки космосу та темні сили, які прагнуть увійти у світло зір. Вони зіштовхнуться зі загадковими цивілізацями, які керуються власними правилами та законами, і виявляться перед величезними моралі, що випробують їхні віру та волю. Їхні відкриття можуть змінити не лише їхнє розуміння світу, але й вплинути на хід історії всього магістеріуму, руйнуючи чи відтворюючи існуючий порядок речей.'),
    ('Космічні мандри', 'Приєднуйтесь до експедиції, яка вирушає у захоплюючі космічні мандри до інших планет. Дізнайтеся про неймовірні пригоди та виклики, з якими стикаються дослідники космосу. Головний персонаж, космічний пілот Емілія, вирушає на свій перший космічний корабель у далеку галактику, де вона зіткнеться з небезпеками та загадками, які перевернуть її уявлення про Всесвіт. Проте, кожна зірка, кожна планета приховує свої таємниці, і кожна зустріч може виявитися переломною для подальших долень героїв. Вони будуть протистояти не лише небезпецям космічного простору, але й власним демонам та перешкодам, які знаходяться в їхніх серцях та душах.');
INSERT INTO Authors (name, address)
VALUES
    ('Марія Іванова', 'Україна, Харків, вул. Сумська, 10. Марія Іванова - відома письменниця, яка прославилася своїми романами та оповіданнями про життя великого міста. Її твори переповнені глибоким розумінням людської долі та відображають складні взаємини між героями у сучасному світі.'),
    ('Андрій Петров', 'Україна, Дніпро, вул. Карла Маркса, 25. Андрій Петров - автор багатьох книг у жанрі наукової фантастики, який здобув популярність завдяки своїй унікальній уяві та таланту створювати фантастичні світи, що захоплюють читачів на кожній сторінці.'),
    ('Євгенія Сидорчук', 'Україна, Луцьк, вул. Відродження, 5. Євгенія Сидорчук - письменниця-фантастка, яка славиться своїми захоплюючими історіями про подорожі у часі та просторі. Її твори вражають своєю оригінальністю та несподіваністю сюжетних поворотів, залишаючи читачів в захваті від кожного рядка.'),
    ('Олег Ковальов', 'Україна, Запоріжжя, вул. Металургів, 8. Олег Ковальов - автор книг у жанрі детективу, який відомий своїми вміннями створювати заплутані сюжети та незабутні образи детективів. Його книги завжди тримають читачів в напрузі до останньої сторінки, розкриваючи таємниці злочинів і несподівані злочинні інтриги.'),
    ('Олександр Гончар', 'Україна, Київ, вул. Шевченка, 15. Олександр Гончар - відомий український письменник, чиї твори завжди вражають своєю глибиною та майстерністю. Його творчість має великий вплив на культуру та літературу України. Його стиль письма характеризується витонченим описом образів і вмінням створювати напружені сюжети, які тримають читача в напрузі до останньої сторінки.'),
    ('Людмила Коваленко', 'Україна, Львів, вул. Франка, 27. Людмила Коваленко - талановитий автор, яка відзначається своєю виразністю та образністю у своїх творах. Вона пише з любовю до своєї країни та історії. Її оповідання вражають читача своєю емоційною силою та здатністю передати атмосферу подій, які розгортаються перед героями.'),
    ('Петро Сидоренко', 'Україна, Одеса, вул. Дерибасівська, 10. Петро Сидоренко - популярний письменник, який привертає увагу своїми цікавими сюжетами та живими образами. Він відомий своїми творами у жанрі пригодницької літератури та фантастики. Його книги завжди створюють атмосферу захоплення та пригод, а персонажі відзначаються своєю живучістю та реалістичністю.'),
    ('Наталія Семенова', 'Україна, Івано-Франківськ, вул. Грушевського, 12. Наталія Семенова - авторка книг для дітей та молоді, яка чарує своїх читачів казковими світами та неперевершеними пригодами. Її твори відомі своєю теплотою та мудрістю, які проникають серця читачів усіх вікових категорій.'),
    ('Ігор Литвиненко', 'Україна, Чернігів, вул. Шевченка, 30. Ігор Литвиненко - популярний письменник у жанрі фентезі, який створює захоплюючі світи магії та пригод. Він відомий своїми колоритними образами та складними сюжетами, які завжди залишають читачів в захваті.'),
    ('Марина Сидоренко', 'Україна, Херсон, вул. Лермонтова, 5. Марина Сидоренко - авторка романів у жанрі романтики та мелодрами, яка вміло розкриває найтонші переживання та емоції своїх персонажів. Її книги завжди підносять настрій та залишають незабутні враження.'),
    ('Євген Петренко', 'Україна, Житомир, вул. Соборна, 17. Євген Петренко - майстер детективного жанру, який відомий своїми заплутаними сюжетами та несподіваними розвязками. Він перевершує уяву своїх читачів, розкриваючи таємниці та вирішуючи злочини влучними детективними методами.');
INSERT INTO Title (name, description)
VALUES
    ('Володар фантазії', 'Цей титул належить авторові, який увійшов у світову літературу як справжній володар фантазії. Його книги переносять читачів у неймовірні світи, де межа між реальністю та магією розпливається. Читачі захоплюються яскравими образами та непередбачуваними сюжетними поворотами.'),
    ('Версія майбутнього', 'Цей титул відноситься до авторів, які створюють захоплюючі науково-фантастичні твори, що розкривають альтернативні версії майбутнього. Їхні книги надихають на роздуми про можливості розвитку технологій та суспільства, а також відображають глибокі філософські та етичні аспекти.'),
    ('Легендарний світобудова', 'Цей титул належить письменнику, який відомий своєю здатністю створювати власні, неймовірні світи з унікальною історією, культурою та міфологією. Він увійшов до історії літератури як справжній майстер світобудови, який здатен звести уявний світ до реальності перед очима читачів.'),
    ('Повелитель темряви', 'Цей титул належить авторові, чиї твори переповнені містикою та хаосом. Він вміло відтворює образи темряви та викликає в читачів почуття тривоги та непередбачуваності. Його книги стають справжнім викликом для тих, хто сміливо вирушає у світ темних сил.'),
    ('Хроніки бунтівника', 'Цей титул відноситься до авторів, які розкривають історії бунтів та повстань проти упорядкованих суспільних систем. Вони надихають читачів на роздуми про справедливість та свободу, а також викликають до дії проти несправедливості та тирани.'),
    ('Владика загадок', 'Цей титул належить майстру загадок та інтриг. Він уміло розкриває таємниці та переплітає сюжетні лінії так, що читачі ніколи не можуть передбачити розвязку. Його книги стають справжнім викликом для розуму та логіки, залишаючи за собою слід загадок, які читачам залишиться розгадувати.'),
    ('Архітектор сновидінь', 'Цей титул відноситься до авторів, які створюють власні світи у світі сновидінь та фантазій. Вони майстерно переносять читачів у захоплюючі подорожі крізь відвідані таємні місця і неймовірні пригоди, де кожен сон стає реальністю.'),
    ('Провідник часу', 'Цей титул належить авторові, який відкриває перед читачами двері у світ минулого та майбутнього. Він вміло переносить читачів через епохи та цивілізації, розкриваючи таємниці минулого та представляючи можливості майбутнього.'),
    ('Творець реальності', 'Цей титул відноситься до письменника, який має здатність створювати нові реальності та уявні світи з непередбачуваними законами та правилами. Він викликає читачів на подорож у світ, де все можливо, а кожен крок приносить нові відкриття та виклики.'),
    ('Колекціонер мрій', 'Цей титул належить авторові, який вміло збирає мрії та фантазії, перетворюючи їх на неповторні твори мистецтва. Він пропонує читачам відпочинок від реальності та можливість зануритися в світ фантазії та уяви, де кожна мрія стає реальністю.'),
    ('Легендарний поборник', 'Цей титул відноситься до авторів, які розповідають історії про героїв та героїнь, які стають символами боротьби за справедливість, свободу та істину. Вони надихають читачів на великі справи та допомагають їм зрозуміти важливість ідеалів та цінностей.'),
    ('Шепіт мрій', 'Цей титул відноситься до авторів, чиї твори переповнені поетичною красою та глибокими філософськими думками. Вони майстерно використовують мову, щоб передати емоційну та духовну сутність своїх персонажів та світів, запрошуючи читачів у мандрівку крізь казкові ландшафти і складні думки.'),
    ('Колекціонер загадок', 'Цей титул належить письменнику, який славиться своїм вмінням створювати заплутані сюжети та непередбачувані сюжетні повороти. Він майстерно розкриває загадки та містичні таємниці, залишаючи читачів на межі своїх мислень та запрошуючи їх на захоплюючу подорож у світ загадок і таємниць.');
